import './User.css';
import Head from './../Headings/Head';
import { useState } from 'react';

export default function GroceryTable({props,deletefun,bill}){
    const [isEdit,setIsEdit]=useState(false);
    const [eName,setEName]=useState(false);
    const [eQty,setEQty]=useState(false);
    const [ePrice,setEPrice]=useState(false);


    return(
        <table className="table">
            <caption><Head head="Users List"/></caption>
            <thead>
                <th>Name</th>
                <th>Qundity</th>
                <th>Price</th>
                <th>Total</th>
                <th>Tex</th>
                <th>Net Amount</th>
                <th>Action</th>
            </thead>
            <tbody>
                {props.map((val,i)=>{
                    return( 
                        <tr key={val.Id}>
                            <td>{isEdit?<input type='text' value={eName}/>:val.Name}</td>
                            <td>{isEdit?<input type='text' value={eQ}/>:val.Qty}</td>
                            <td>{isEdit?<input type='text'/>:val.Price}</td>
                            <td>{isEdit?<input type='text'disabled/>:val.Total}</td>
                            <td>{isEdit?<input type='text'disabled/>:val.Tex}</td>
                            <td>{isEdit?<input type='text'disabled/>:val.Net}</td>
                            <td>
                                <button onClick={()=>deletefun(val.Id)} >delete</button>
                                <button onClick={()=>{setIsEdit(true)}}>{isEdit?'Save':'Edit'}</button>
                            </td>
                        </tr>
                    );
                })}
                <tr>
                    <td colSpan={5}>total bill</td>
                    <td>{bill}</td>
                </tr>
            </tbody>
        </table>  
    )
}
