import './User.css';
import Head from './../Headings/Head';
import { useState } from 'react';

export default function GroceryTable({props,deletefun,bill}){
    const [isEdit,setIsEdit]=useState(false);
    return(
        <table className="table">
            <caption><Head head="Users List"/></caption>
            <thead>
                <th>Name</th>
                <th>Qundity</th>
                <th>Price</th>
                <th>Total</th>
                <th>Tex</th>
                <th>Net Amount</th>
                <th>Action</th>
            </thead>
            <tbody>
                {props.map((val,i)=>{
                    return( 
                        <tr key={i}>
                            <td>{isEdit?<input/>:val.Name}</td>
                            <td>{val.Qty}</td>
                            <td>{val.Price}</td>
                            <td>{val.Total}</td>
                            <td>{val.Tex}</td>
                            <td>{val.Net}</td>
                            <td>
                                <button onClick={()=>deletefun(val.Id)} >delete</button>
                                <button onClick={()=>{setIsEdit(true)}}>Edit</button>
                            </td>
                        </tr>
                    );
                })}
                <tr>
                    <td colSpan={5}>total bill</td>
                    <td>{bill}</td>
                </tr>
            </tbody>
        </table>  
    )
}
