import './User.css';
import Head from './../Headings/Head';
import { useEffect, useState } from 'react';

export default function GroceryTable({ props, deletefun, bill}) {
  const [isEdit, setIsEdit] = useState(false);
  const [Item, setItem] = useState({});

    function saveDetails(id) {
         setIsEdit(!isEdit);
    }

  return (
    <table className="table">
      <caption><Head head="Users List" /></caption>
      <thead>
        <tr>
            <th>Id</th>
          <th>Name</th>
          <th>Quantity</th>
          <th>Price</th>
          <th>Total</th>
          <th>Tax</th>
          <th>Net Amount</th>
          <th>Action</th>
        </tr>
      </thead>
      <tbody>
        {props.map((val) => {
          return (
            <tr key={val.Id}>
            <td>{isEdit ? <input type='text' value={Item.Id}  onChange={(e) => setItem({ ...Item, Id: e.target.value})} /> : val.Id}</td>
              <td>{isEdit ? <input type='text' value={Item.Name}  onChange={(e) => setItem({ ...Item, Name: e.target.value})} /> : val.Name}</td>
              <td>{isEdit ? <input type='number' value={Item.Qty}  onChange={(e) => setItem({ ...Item, Qty: e.target.value })} /> : val.Qty}</td>
              <td>{isEdit ? <input type='number' value={Item.Price} placeholder={val.Price} onChange={(e) => setItem({ ...Item, Price: e.target.value })} /> : val.Price}</td>
              <td>{isEdit ? <input type='number' disabled value={Item.Total} /> : val.Total}</td>
              <td>{isEdit ? <input type='number' disabled value={Item.Tex} /> : val.Tex}</td>
              <td>{isEdit ? <input type='number' disabled value={Item.Net} /> : val.Net}</td>
              <td>
                <button onClick={() => deletefun(val.Id)}>Delete</button>
                <button onClick={saveDetails(val.id)}>{isEdit ? 'Save' : 'Edit'}</button>
              </td>
            </tr>
          );
        })}
        <tr>
          <td colSpan={5}>Total bill</td>
          <td>{bill}</td>
        </tr>
      </tbody>
    </table>
  );
}
