import './User.css';
import Head from './../Headings/Head';
import { useEffect, useState } from 'react';

export default function GroceryTable({ props, deletefun, bill,editedData }) {
  const [isEdit, setIsEdit] = useState(false);
  const [editedItem, setEditedItem] = useState({});

  useEffect(() => {
    setItem({
      Id: Item.Id,
      Name: Item.Name,
      Qty: Item.Qty,
      Price: editedItem.Price,
      Total: editedItem.Qty * editedItem.Price,
      Tex: (editedItem.Qty * editedItem.Price) * 0.18,
      Net: (editedItem.Qty * editedItem.Price) * 1.18
    });
  }, [editedItem.Qty, editedItem.Price]);

  function saveDetails() {
    editedData(editedItem);
  }

  return (
    <table className="table">
      <caption><Head head="Users List" /></caption>
      <thead>
        <tr>
          <th>Name</th>
          <th>Quantity</th>
          <th>Price</th>
          <th>Total</th>
          <th>Tax</th>
          <th>Net Amount</th>
          <th>Action</th>
        </tr>
      </thead>
      <tbody>
        {props.map((val) => {
          return (
            <tr key={val.Id}>
              <td>{isEdit ? <input type='text' value={editedItem.Name} onChange={(e) => setEditedItem({ ...editedItem, Name: e.target.value,Id:val.Id })} /> : val.Name}</td>
              <td>{isEdit ? <input type='text' value={editedItem.Qty} onChange={(e) => setEditedItem({ ...editedItem, Qty: e.target.value })} /> : val.Qty}</td>
              <td>{isEdit ? <input type='text' value={editedItem.Price} onChange={(e) => setEditedItem({ ...editedItem, Price: e.target.value })} /> : val.Price}</td>
              <td>{isEdit ? <input type='text' disabled value={editedItem.Total} /> : val.Total}</td>
              <td>{isEdit ? <input type='text' disabled value={editedItem.Tex} /> : val.Tex}</td>
              <td>{isEdit ? <input type='text' disabled value={editedItem.Net} /> : val.Net}</td>
              <td>
                <button onClick={() => deletefun(val.Id)}>Delete</button>
                <button onClick={() => { setIsEdit(!isEdit); saveDetails() }}>{isEdit ? 'Save' : 'Edit'}</button>
              </td>
            </tr>
          );
        })}
        <tr>
          <td colSpan={5}>Total bill</td>
          <td>{bill}</td>
        </tr>
      </tbody>
    </table>
  );
}
