import './User.css';
import Head from './../Headings/Head';
import { useEffect, useState } from 'react';

export default function GroceryTable({props,deletefun,bill}){
    const [isEdit,setIsEdit]=useState(false);
    const [eName,setEName]=useState("");
    const [eQty,setEQty]=useState("");
    const [ePrice,setEPrice]=useState("");
    const [eTotal,setETotal]=useState("");
    const [eTex,setETex]=useState("");
    const [eNet,setENet]=useState("");
    const [eId,setEId]=useState("");
    useEffect(()=>setETotal(eQty*ePrice));
    useEffect(()=>setETex(eTotal*0.18));
    useEffect(()=>setENet(eTotal+eTex));
    function details(){
        const obj={
            Id:eId,
            Name:eName,
            Qty:eQty,
            Price:ePrice,
            Total:eTotal,
            Tex:eTex,
            Net:eNet
        }
    }

    return(
        <table className="table">
            <caption><Head head="Users List"/></caption>
            <thead>
                <th>Name</th>
                <th>Qundity</th>
                <th>Price</th>
                <th>Total</th>
                <th>Tex</th>
                <th>Net Amount</th>
                <th>Action</th>
            </thead>
            <tbody>
                {props.map((val,i)=>{
                    return( 
                        <tr key={val.Id}>
                            <td>{isEdit?<input type='text' value={val.Name} onChange={(e)=>setEName(e.target.value)}/>:val.Name}</td>
                            <td>{isEdit?<input type='text' value={val.Qty} onChange={(e)=>setEQty(e.target.value)}/>:val.Qty}</td>
                            <td>{isEdit?<input type='text' value={val.Price} onChange={(e)=>setEPrice(e.target.value)}/>:val.Price}</td>
                            <td>{isEdit?<input type='text'disabled value={val.Total} />:val.Total}</td>
                            <td>{isEdit?<input type='text'disabled value={val.Tex} />:val.Tex}</td>
                            <td>{isEdit?<input type='text'disabled value={val.Net} />:val.Net}</td>
                            <td>
                                <button onClick={()=>deletefun(val.Id)} >delete</button>
                                <button onClick={()=>{setIsEdit(true)}}>{isEdit?'Save':'Edit'}</button>
                            </td>
                        </tr>
                    );
                })}
                <tr>
                    <td colSpan={5}>total bill</td>
                    <td>{bill}</td>
                </tr>
            </tbody>
        </table>  
    )
}
